/*     Height matcher (no jQuery)     */
/* +------------- USAGE ------------+ */
/* |                                | */
/* | heightMatcher('.exampleClass') | */
/* |                                | */
/* +--------------------------------+ */
function osTop(element){const de=document.documentElement;const box=element.getBoundingClientRect();return box.top+window.pageYOffset-de.clientTop}
function heightMatcher(tg){let h=0;let c=0;let ci=-1;let s=!1;let e=[];let a=document.getElementsByClassName(tg.replace(/[^A-Za-z0-9\-\_]/g,''));for(i=0;i<a.length;i++){if(!s){h=a[i].clientHeight;c=osTop(a[i]);s=!0}
if(e.length&&c!==osTop(a[i])&&i!==a.length){for(n=0;n<e[ci].el.length;n++){e[ci].el[n].style.height=e[ci].t+'px'}}
if(e.length&&e[ci].p===osTop(a[i])){if(a[i].clientHeight>h){e[ci].t=a[i].clientHeight;h=a[i].clientHeight}}else{ci++;c=osTop(a[i]);h=a[i].scrollHeight;e.push({p:c,t:h});e[ci].el=[]}
e[ci].el.push(a[i]);if(i+1===a.length){ci;for(n=0;n<e[ci].el.length;n++){e[ci].el[n].style.height=e[ci].t+'px'}}}}